import React from "react";
import GroupIcon from "@material-ui/icons/Group";
import {
  Avatar,
  Box,
  Button,
  Divider,
  Drawer,
  Hidden,
  List,
  Typography,
  makeStyles,
  useTheme,
} from "@material-ui/core";
import userAvatar from "../../assets/avatarTix.jpg";
import NavItem from "./NavItem";
import { useDispatch, useSelector } from "react-redux";
import TheatersIcon from "@material-ui/icons/Theaters";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import Swal from "sweetalert2";

const items = [
  {
    href: "/manage-user",
    icon: GroupIcon,
    title: "Quản lí người dùng",
  },
  {
    href: "/manage-movie",
    icon: TheatersIcon,
    title: "Quản lí phim",
  },
];

const useStyles = makeStyles((theme) => ({
  mobileDrawer: {
    width: 256,
  },
  desktopDrawer: {
    width: 256,
    top: 64,
    height: "calc(100% - 64px)",
  },
  avatar: {
    cursor: "pointer",
    width: 64,
    height: 64,
    marginBottom: theme.spacing(1),
  },
}));

const NavBarAdmin = ({ openDrawer, setOpenDrawer }) => {
  const classes = useStyles();
  const theme = useTheme();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.currentUserReducer);

  const content = (
    <Box height="100%" display="flex" flexDirection="column">
      <Box alignItems="center" display="flex" flexDirection="column" p={2}>
        <Avatar alt="Avatar" className={classes.avatar} src={userAvatar} />
        <Typography
          gutterBottom
          className={classes.name}
          color="textPrimary"
          variant="h4"
        >
          {user.taiKhoan}
        </Typography>
        <Typography color="textSecondary" variant="body2">
          {user.email}
        </Typography>
      </Box>
      <Divider />
      <Box p={2}>
        <List>
          {items.map((item) => (
            <NavItem
              href={item.href}
              key={item.title}
              title={item.title}
              icon={item.icon}
            />
          ))}
        </List>
      </Box>

      <Divider />

      <Box p={2}>
        <Button
          style={{
            color: theme.palette.text.secondary,
            fontWeight: theme.typography.fontWeightMedium,
            justifyContent: "flex-start",
            letterSpacing: 0,
            padding: "10px 8px",
            textTransform: "none",
            width: "100%",
          }}
          onClick={() => {
            Swal.fire({
              title: "Bạn có muốn đăng xuất ?",
              showCancelButton: true,
              confirmButtonText: `Đồng ý`,
              cancelButtonText: "Hủy",
              icon: "question",
            }).then((result) => {
              if (result.isConfirmed) {
                Swal.fire({
                  title: "Đã đăng xuất",
                  text: "Cảm ơn bạn đã sử dụng TIX!",
                  icon: "success",
                  timer: "2000",
                });
                localStorage.removeItem("currentUser");
                dispatch({
                  type: "LOG-OUT",
                  payload: null,
                });
              }
            });
          }}
        >
          <ExitToAppIcon style={{ marginRight: theme.spacing(1) }} />
          <span
            style={{
              marginRight: "auto",
            }}
          >
            Đăng xuất
          </span>
        </Button>
      </Box>

      <Divider />

      <Box flexGrow={1} />
    </Box>
  );

  return (
    <>
      <Hidden lgUp>
        <Drawer
          anchor="left"
          classes={{ paper: classes.mobileDrawer }}
          variant="temporary"
          open={openDrawer}
          onClose={() => {
            setOpenDrawer(false);
          }}
        >
          {content}
        </Drawer>
      </Hidden>
      <Hidden mdDown>
        <Drawer
          anchor="left"
          classes={{ paper: classes.desktopDrawer }}
          open
          variant="persistent"
        >
          {content}
        </Drawer>
      </Hidden>
    </>
  );
};

export default NavBarAdmin;
